using MongoDB.Driver;
using System;
using System.Collections.Generic;
using System.Configuration;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using MongoDB;
using MongoDB.Bson;

namespace SNS.Benchmark.Runner.NoSql
{
    internal class MongoContext
    {
        private MongoClient mongoClient;
        public IMongoDatabase DataBase
        {
            get
            {
                return this.mongoClient.GetDatabase(Settings.Default.MongoDBName);
            }
        }
        //Credenziali
        //http://mongodb.github.io/mongo-csharp-driver/2.0/reference/driver/authentication/
        private static MongoContext CreateNewContext()
        {

            MongoContext ctx = new MongoContext();
            ctx.mongoClient = new MongoClient(Settings.Default.MongoConnectionString);
      
            return ctx;
        }

        private static MongoContext current;
        public static MongoContext Current
        {
            get
            {
                return current ?? (current = CreateNewContext());
            }
        }

        public void CreateCollectionIfNotExists(string CollectionName)
        {
            var lists = MongoContext.Current.DataBase.ListCollections().ToList();
            if (!lists.Any(x => CollectionName.Equals(x[0].ToString())))
            {
             this.DataBase.CreateCollection(CollectionName);
            }
        }
    }
}
